import 'package:bloc/bloc.dart';import 'package:equatable/equatable.dart';import 'package:flutter_app_mock/data/food_model.dart';import 'package:flutter_app_mock/data/global_data.dart';import 'package:flutter_app_mock/domain/api_services.dart';import 'package:flutter_app_mock/presentation/helper_widgets/toast.dart';part 'home_bloc_state.dart';class HomeBlocCubit extends Cubit<HomeBlocState> {  HomeBlocCubit() : super(HomeBlocInitial());  getApprovedFoodList() async {    try {      emit(Loading());      if (GlobalData.approvedFoodModel?.categories == null) {        var res = await ApiServices().getFoodList();        print('test');        GlobalData.approvedFoodModel = approvedFoodModelFromMap(res.body);        emit(ApprovedFoodResponse());      } else {        emit(ApprovedFoodResponse());      }    } catch (e) {      print(e);      showToast(msg: 'error');      emit(DataError(e));    }  }}