import 'package:flutter/material.dart';import 'package:flutter_app_mock/data/global_data.dart';import 'package:flutter_app_mock/presentation/helper_widgets/hex_color.dart';import 'helper_widgets/ui_helper.dart';///item cardWidget getListItem() => ListView.builder(      shrinkWrap: true,      itemCount: GlobalData.approvedFoodModel?.categories?.length ?? 0,      itemBuilder: (context, index) => Padding(        padding: const EdgeInsets.symmetric(vertical: 6),        child: Card(          elevation: 2,          child: Padding(            padding: const EdgeInsets.symmetric(vertical: 3),            child: ExpansionTile(              leading: Image.asset(                'assets/healthy_food.png',                width: 45,                height: 45,              ),              title: RichText(                text: TextSpan(                  children: [                    TextSpan(                      text: GlobalData.approvedFoodModel?.categories[index]                              .category?.categoryName ??                          '',                      style: Theme.of(context).textTheme.subtitle1.copyWith(                          color: HexColor(GlobalData.approvedFoodModel                              ?.categories[index].category?.colorCode)),                    ),                    TextSpan(                      text: GlobalData.approvedFoodModel?.categories[index]                                  .category?.servingSize !=                              null                          ? ' ( ' +                              GlobalData.approvedFoodModel?.categories[index]                                  .category?.servingSize +                              ' )'                          : '',                      style: Theme.of(context).textTheme.bodyText2,                    ),                  ],                ),              ),              ///subcategory              children: List.generate(                GlobalData.approvedFoodModel?.categories[index].category                        ?.subcategories?.length ??                    0,                (index2) => Padding(                  padding:                      const EdgeInsets.symmetric(vertical: 8, horizontal: 14),                  child: Column(                    crossAxisAlignment: CrossAxisAlignment.start,                    children: [                      Text(                        GlobalData.approvedFoodModel?.categories[index].category                            ?.subcategories[index2].subCategoryname,                        style: Theme.of(context).textTheme.subtitle1.copyWith(                            color: HexColor(GlobalData.approvedFoodModel                                ?.categories[index].category?.colorCode)),                      ),                      verticalSpaceSmall,                      ///item                      ListView.builder(                        physics: NeverScrollableScrollPhysics(),                        shrinkWrap: true,                        itemCount: GlobalData                            .approvedFoodModel                            ?.categories[index]                            .category                            ?.subcategories[index2]                            .items                            .length,                        itemBuilder: (context, index3) => Column(                          crossAxisAlignment: CrossAxisAlignment.start,                          children: [                            Text(                              GlobalData                                  .approvedFoodModel                                  ?.categories[index]                                  .category                                  ?.subcategories[index2]                                  .items[index3],                              style: Theme.of(context).textTheme.bodyText2,                            ),                            Divider(),                          ],                        ),                      )                    ],                  ),                ),              ),            ),          ),        ),      ),    );